mapper.update:
update ps_board set
  title=#{title},
  content=#{content}
where 
  category=#{category}
  and board_no=#{no}
  and writer=#{writer.no}

update ps_board set
  view_count=#{viewCount}
where
  category=#{category}
  and board_no=#{no}

mapper.delete:
delete from ps_board
where 
  category=#{category}
  and board_no=#{no}
  and writer=#{writer.no}
